import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import './FormattedTable.css';

const FormattedTable = () => {
    const location = useLocation();
    const { data, boldRows, checkboxRows } = location.state;
    const [checkedRows, setCheckedRows] = useState([]); // Initialize as empty array
    const [filter, setFilter] = useState('all'); // State to track the current filter
    const [confirmReset, setConfirmReset] = useState(0); // State to track reset confirmation

    const handleCheckboxClick = (index) => {
        setCheckedRows((prevCheckedRows) => {
            if (prevCheckedRows.includes(index)) {
                return prevCheckedRows.filter((i) => i !== index);
            } else {
                return [...prevCheckedRows, index];
            }
        });
    };

    const handleReset = () => {
        if (confirmReset === 0) {
            setConfirmReset(1);
        } else if (confirmReset === 1) {
            setConfirmReset(2);
        } else if (confirmReset === 2) {
            setCheckedRows([]);
            setConfirmReset(0);
        }
    };

    const handleFilterChange = (event) => {
        setFilter(event.target.value);
    };

    const getFilteredData = () => {
        switch (filter) {
            case 'checked':
                return data.filter((_, index) => checkedRows.includes(index) && checkboxRows.includes(index));
            case 'unchecked':
                return data.filter((_, index) => !checkedRows.includes(index) && checkboxRows.includes(index));
            case 'all':
            default:
                return data;
        }
    };

    const filteredData = getFilteredData();
    const totalCheckboxRows = checkboxRows.length;
    const checkedCount = checkedRows.filter(index => checkboxRows.includes(index)).length;
    const leftOutCount = totalCheckboxRows - checkedCount;

    return (
        <div className="formatted-table-container">
            <div className="info-boxes">
                <div className="info-box">
                    <h3>Total Events</h3>
                    <p>{totalCheckboxRows}</p>
                </div>
                <div className="info-box">
                    <h3>Completed Events</h3>
                    <p>{checkedCount}</p>
                </div>
                <div className="info-box">
                    <h3>Leftout Events</h3>
                    <p>{leftOutCount}</p>
                </div>
            </div>
            <div className="filter-container">
                <label htmlFor="filter">Filter Activities: </label>
                <select id="filter" value={filter} onChange={handleFilterChange}>
                    <option value="all">All Activities</option>
                    <option value="checked">Checked Activities</option>
                    <option value="unchecked">Unchecked Activities</option>
                </select>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>Hrs</th>
                        <th>Min</th>
                        <th>Sec</th>
                        <th>Si. No</th>
                        <th>Activity Name</th>
                        <th>Confirmed By</th>
                        <th>Check</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredData.map((row, index) => (
                        <tr key={index} style={{ fontWeight: boldRows.includes(index) ? 'bold' : 'normal' }}>
                            <td>{row.hrs}</td>
                            <td>{row.mins}</td>
                            <td>{row.sec}</td>
                            <td>{row['si.no']}</td>
                            <td>{row.activity_name}</td>
                            <td>{row.confirmed_by}</td>
                            <td>
                                {checkboxRows.includes(index) && (
                                    <input 
                                        type="checkbox" 
                                        checked={checkedRows.includes(index)} 
                                        onChange={() => handleCheckboxClick(index)}
                                    />
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="progress-container">
                <label>Progress:</label>
                <progress value={(checkedCount / totalCheckboxRows) * 100} max="100"></progress>
            </div>
            <button className="reset-button" onClick={handleReset}>
                {confirmReset === 1 ? "Are you sure you want to reset?" : 
                 confirmReset === 2 ? "Are you really sure you want to reset?" : 
                 "Reset"}
            </button>
        </div>
    );
};

export default FormattedTable;
