import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './FormattedTable.css';

const FormattedTable = () => {
    const location = useLocation();
    const { data, boldRows, checkboxRows } = location.state;
    const [checkedRows, setCheckedRows] = useState([]);
    const [progress, setProgress] = useState(0);

    useEffect(() => {
        setProgress((checkedRows.length / checkboxRows.length) * 100);
    }, [checkedRows, checkboxRows]);

    const handleCheckboxToggle = (index) => {
        setCheckedRows(prev => prev.includes(index) ? prev.filter(i => i !== index) : [...prev, index]);
    };

    return (
        <div className="formatted-table-container">
            <table>
                <thead>
                    <tr>
                        <th>Hrs</th>
                        <th>Min</th>
                        <th>Sec</th>
                        <th>Si. No</th>
                        <th>Activity Name</th>
                        <th>Confirmed By</th>
                        <th>Check</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((row, index) => (
                        <tr 
                            key={index} 
                            style={{ 
                                fontWeight: boldRows.includes(index) ? 'bold' : 'normal'
                            }}
                        >
                            <td>{row.hours}</td>
                            <td>{row.minutes}</td>
                            <td>{row.seconds}</td>
                            <td>{row.serialNumber}</td>
                            <td>{row.activityName}</td>
                            <td>{row.confirmedBy}</td>
                            {checkboxRows.includes(index) && (
                                <td>
                                    <input 
                                        type="checkbox" 
                                        checked={checkedRows.includes(index)} 
                                        onChange={() => handleCheckboxToggle(index)} 
                                    />
                                </td>
                            )}
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="progress-container">
                <label>Progress:</label>
                <progress value={progress} max="100"></progress>
            </div>
        </div>
    );
};

export default FormattedTable;
