import React, { useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { Table, TableHead, TableRow, TableCell, Checkbox, Button, Paper, Box, Typography, TableContainer, TableBody, FormControl, InputLabel, Select, MenuItem } from '@mui/material';
import { VariableSizeList as List } from 'react-window';
import './TableSelection.css';

const TableSelection = ({ data = [] }) => { // Default value for data
    const [boldRows, setBoldRows] = useState([]);
    const [checkboxRows, setCheckboxRows] = useState([]);
    const [filter, setFilter] = useState('all');
    const navigate = useNavigate();

    const handleBoldClick = (id) => {
        setBoldRows((prevBoldRows) => {
            if (prevBoldRows.includes(id)) {
                return prevBoldRows.filter((i) => i !== id);
            } else {
                return [...prevBoldRows, id];
            }
        });
    };

    const handleCheckboxClick = (id) => {
        setCheckboxRows((prevCheckboxRows) => {
            if (prevCheckboxRows.includes(id)) {
                return prevCheckboxRows.filter((i) => i !== id);
            } else {
                return [...prevCheckboxRows, id];
            }
        });
    };

    const handleFilterChange = (event) => {
        setFilter(event.target.value);
    };

    const getFilteredData = () => {
        switch (filter) {
            case 'bold':
                return data.filter((row) => boldRows.includes(row.id));
            case 'checkbox':
                return data.filter((row) => checkboxRows.includes(row.id));
            case 'all':
            default:
                return data;
        }
    };

    const filteredData = getFilteredData();

    const Row = ({ index, style }) => {
        const row = filteredData[index];
        const isBold = boldRows.includes(row.id);
        return (
            <TableRow key={row.id} style={style} className={isBold ? 'bold-row' : ''}>
                <TableCell className="table-cell">{row.hrs}</TableCell>
                <TableCell className="table-cell">{row.mins}</TableCell>
                <TableCell className="table-cell">{row.sec}</TableCell>
                <TableCell className="table-cell">{row['si.no']}</TableCell>
                <TableCell className="table-cell">{row.activity_name}</TableCell>
                <TableCell className="table-cell">{row.confirmed_by}</TableCell>
                <TableCell className="table-cell">
                    <Checkbox 
                        checked={boldRows.includes(row.id)} 
                        onChange={() => handleBoldClick(row.id)}
                    />
                </TableCell>
                <TableCell className="table-cell">
                    <Checkbox 
                        checked={checkboxRows.includes(row.id)} 
                        onChange={() => handleCheckboxClick(row.id)}
                    />
                </TableCell>
            </TableRow>
        );
    };

    const getItemSize = useCallback(() => 60, []); // Increased item size for better spacing

    const handleProceed = () => {
        navigate('/formatted-table', { state: { data, boldRows, checkboxRows } });
    };

    return (
        <Box sx={{ padding: 2, paddingBottom: '60px' }}>
            <FormControl fullWidth sx={{ marginBottom: 2 }}>
                <InputLabel id="filter-label">Filter Activities</InputLabel>
                <Select
                    labelId="filter-label"
                    id="filter"
                    value={filter}
                    label="Filter Activities"
                    onChange={handleFilterChange}
                >
                    <MenuItem value="all">All Activities</MenuItem>
                    <MenuItem value="bold">Bold Activities</MenuItem>
                    <MenuItem value="checkbox">Checkbox Activities</MenuItem>
                </Select>
            </FormControl>
            <TableContainer component={Paper} sx={{ height: 600, width: '100%' }}>
                <Table stickyHeader sx={{ minWidth: 650 }}>
                    <TableHead>
                        <TableRow>
                            <TableCell>Hrs</TableCell>
                            <TableCell>Min</TableCell>
                            <TableCell>Sec</TableCell>
                            <TableCell>Si. No</TableCell>
                            <TableCell>Activity Name</TableCell>
                            <TableCell>Confirmed By</TableCell>
                            <TableCell>Bold</TableCell>
                            <TableCell>Checkbox</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        <List
                            height={600}
                            itemCount={filteredData.length}
                            itemSize={getItemSize}
                            width="100%"
                            outerElementType={outerElementType}
                            innerElementType={innerElementType}
                        >
                            {({ index, style }) => <Row index={index} style={style} />}
                        </List>
                    </TableBody>
                </Table>
            </TableContainer>
            <Button 
                variant="contained" 
                color="primary" 
                onClick={handleProceed} 
                sx={{ marginTop: 2 }}
            >
                Proceed
            </Button>
        </Box>
    );
};

export default TableSelection;
