import React, { useState } from 'react';
import { useHistory, useLocation } from 'react-router-dom';

const TableSelection = () => {
    const location = useLocation();
    const history = useHistory();
    const [boldRows, setBoldRows] = useState([]);
    const [checkboxRows, setCheckboxRows] = useState([]);
    const data = location.state.data;

    const handleBoldToggle = (index) => {
        setBoldRows(prev => prev.includes(index) ? prev.filter(i => i !== index) : [...prev, index]);
    };

    const handleCheckboxToggle = (index) => {
        setCheckboxRows(prev => prev.includes(index) ? prev.filter(i => i !== index) : [...prev, index]);
    };

    const handleSubmit = () => {
        history.push({
            pathname: '/formatted',
            state: { data, boldRows, checkboxRows }
        });
    };

    return (
        <div>
            <table>
                <thead>
                    <tr>
                        <th>Hrs</th>
                        <th>Min</th>
                        <th>Sec</th>
                        <th>Si. No</th>
                        <th>Activity Name</th>
                        <th>Confirmed By</th>
                        <th>Bold</th>
                        <th>Checkbox</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((row, index) => (
                        <tr key={index}>
                            <td>{row.hours}</td>
                            <td>{row.minutes}</td>
                            <td>{row.seconds}</td>
                            <td>{row.serialNumber}</td>
                            <td>{row.activityName}</td>
                            <td>{row.confirmedBy}</td>
                            <td>
                                <input 
                                    type="checkbox" 
                                    checked={boldRows.includes(index)} 
                                    onChange={() => handleBoldToggle(index)} 
                                />
                            </td>
                            <td>
                                <input 
                                    type="checkbox" 
                                    checked={checkboxRows.includes(index)} 
                                    onChange={() => handleCheckboxToggle(index)} 
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={handleSubmit}>Submit</button>
        </div>
    );
};

export default TableSelection;
