import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import { Table, TableHead, TableRow, TableCell, Checkbox, Button, Paper, Select, MenuItem, FormControl, InputLabel, Box, Typography, LinearProgress, TableContainer, TableBody } from '@mui/material';
import './FormattedTable.css';

const FormattedTable = () => {
    const location = useLocation();
    const { data, boldRows, checkboxRows } = location.state;
    const [checkedRows, setCheckedRows] = useState([]);
    const [filter, setFilter] = useState('all');
    const [confirmReset, setConfirmReset] = useState(0);

    const handleCheckboxClick = (id) => {
        setCheckedRows((prevCheckedRows) => {
            if (prevCheckedRows.includes(id)) {
                return prevCheckedRows.filter((i) => i !== id);
            } else {
                return [...prevCheckedRows, id];
            }
        });
    };

    const handleReset = () => {
        if (confirmReset === 0) {
            setConfirmReset(1);
        } else if (confirmReset === 1) {
            setConfirmReset(2);
        } else if (confirmReset === 2) {
            setCheckedRows([]);
            setConfirmReset(0);
        }
    };

    const handleFilterChange = (event) => {
        setFilter(event.target.value);
    };

    const getFilteredData = () => {
        switch (filter) {
            case 'checked':
                return data.filter((row) => checkedRows.includes(row.id));
            case 'unchecked':
                return data.filter((row) => !checkedRows.includes(row.id) && checkboxRows.includes(row.id));
            case 'all':
            default:
                return data;
        }
    };

    const filteredData = getFilteredData();
    const totalCheckboxRows = checkboxRows.length;
    const checkedCount = checkedRows.filter(id => checkboxRows.includes(id)).length;
    const leftOutCount = totalCheckboxRows - checkedCount;

    return (
        <Box sx={{ padding: 2, paddingBottom: '60px' }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-around', marginBottom: 2 }}>
                <Box sx={{ textAlign: 'center', padding: 1, backgroundColor: '#f0f0f0', borderRadius: 1 }}>
                    <Typography variant="h6">Total Events</Typography>
                    <Typography variant="h4">{totalCheckboxRows}</Typography>
                </Box>
                <Box sx={{ textAlign: 'center', padding: 1, backgroundColor: '#f0f0f0', borderRadius: 1 }}>
                    <Typography variant="h6">Completed Events</Typography>
                    <Typography variant="h4">{checkedCount}</Typography>
                </Box>
                <Box sx={{ textAlign: 'center', padding: 1, backgroundColor: '#f0f0f0', borderRadius: 1 }}>
                    <Typography variant="h6">Leftout Events</Typography>
                    <Typography variant="h4">{leftOutCount}</Typography>
                </Box>
            </Box>
            <FormControl fullWidth sx={{ marginBottom: 2 }}>
                <InputLabel id="filter-label">Filter Activities</InputLabel>
                <Select
                    labelId="filter-label"
                    id="filter"
                    value={filter}
                    label="Filter Activities"
                    onChange={handleFilterChange}
                >
                    <MenuItem value="all">All Activities</MenuItem>
                    <MenuItem value="checked">Checked Activities</MenuItem>
                    <MenuItem value="unchecked">Unchecked Activities</MenuItem>
                </Select>
            </FormControl>
            <TableContainer component={Paper} sx={{ height: 600, width: '100%' }}>
                <Table stickyHeader sx={{ minWidth: 650 }}>
                    <TableHead>
                        <TableRow>
                            <TableCell>Hrs</TableCell>
                            <TableCell>Min</TableCell>
                            <TableCell>Sec</TableCell>
                            <TableCell>Si. No</TableCell>
                            <TableCell>Activity Name</TableCell>
                            <TableCell>Confirmed By</TableCell>
                            <TableCell>Check</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {filteredData.map((row, index) => (
                            <TableRow key={row.id} className={boldRows.includes(row.id) ? 'bold-row' : ''}>
                                <TableCell>{row.hrs}</TableCell>
                                <TableCell>{row.mins}</TableCell>
                                <TableCell>{row.sec}</TableCell>
                                <TableCell>{row['si.no']}</TableCell>
                                <TableCell>{row.activity_name}</TableCell>
                                <TableCell>{row.confirmed_by}</TableCell>
                                <TableCell>
                                    {checkboxRows.includes(row.id) && (
                                        <Checkbox 
                                            checked={checkedRows.includes(row.id)} 
                                            onChange={() => handleCheckboxClick(row.id)}
                                        />
                                    )}
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
            <Box className="fixed-progress-bar">
                <Typography variant="h6">Progress</Typography>
                <LinearProgress variant="determinate" value={(checkedCount / totalCheckboxRows) * 100} />
            </Box>
            <Button 
                variant="contained" 
                color="secondary" 
                onClick={handleReset} 
                sx={{ marginTop: 2 }}
            >
                {confirmReset === 1 ? "Are you sure you want to reset?" : 
                 confirmReset === 2 ? "Are you really sure you want to reset?" : 
                 "Reset"}
            </Button>
        </Box>
    );
};

export default FormattedTable;
