import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import { Table, TableHead, TableRow, TableCell, Checkbox, Button, Paper, Select, MenuItem, FormControl, InputLabel, Box, Typography, TableContainer, TableBody,AppBar,Toolbar } from '@mui/material';
import './FormattedTable.css';

const FormattedTable = () => {
    const location = useLocation();
    const { data, boldRows, checkboxRows } = location.state;
    const [checkedRows, setCheckedRows] = useState([]);
    const [filter, setFilter] = useState('all');
    const [confirmReset, setConfirmReset] = useState(0);

    const handleCheckboxClick = (id) => {
        setCheckedRows((prevCheckedRows) => {
            if (prevCheckedRows.includes(id)) {
                return prevCheckedRows.filter((i) => i !== id);
            } else {
                return [...prevCheckedRows, id];
            }
        });
    };

    const handleReset = () => {
        if (confirmReset === 0) {
            setConfirmReset(1);
        } else if (confirmReset === 1) {
            setConfirmReset(2);
        } else if (confirmReset === 2) {
            setCheckedRows([]);
            setConfirmReset(0);
        }
    };

    const handleFilterChange = (event) => {
        setFilter(event.target.value);
    };

    const getFilteredData = () => {
        switch (filter) {
            case 'checked':
                return data.filter((row) => checkedRows.includes(row.id));
            case 'unchecked':
                return data.filter((row) => !checkedRows.includes(row.id) && checkboxRows.includes(row.id));
            case 'all':
            default:
                return data;
        }
    };

    const filteredData = getFilteredData();
    const totalCheckboxRows = checkboxRows.length;
    const checkedCount = checkedRows.filter(id => checkboxRows.includes(id)).length;
    const leftOutCount = totalCheckboxRows - checkedCount;
    const progressPercentage = (checkedCount / totalCheckboxRows) * 100;

    const Row = ({ row }) => {
        const isBold = boldRows.includes(row.id);
        return (
            
            <TableRow key={row.id} className={isBold ? 'bold-row' : ''} style={{ fontSize: '1.2em' }}>
                <TableCell className="table-cell" style={{ fontSize: '1.2em' }}>{row.hrs}</TableCell>
                <TableCell className="table-cell" style={{ fontSize: '1.2em' }}>{row.mins}</TableCell>
                <TableCell className="table-cell" style={{ fontSize: '1.2em' }}>{row.sec}</TableCell>
                <TableCell className="table-cell" style={{ fontSize: '1.2em' }}>{row['si.no']}</TableCell>
                <TableCell className="table-cell" style={{ fontSize: '1.2em' }}>{row.activity_name}</TableCell>
                <TableCell className="table-cell" style={{ fontSize: '1.2em' }}>{row.confirmed_by}</TableCell>
                <TableCell className="table-cell" style={{ fontSize: '1.2em' }}>
                    {checkboxRows.includes(row.id) && (
                        <Checkbox 
                            checked={checkedRows.includes(row.id)} 
                            onChange={() => handleCheckboxClick(row.id)}
                        />
                    )}
                </TableCell>
            </TableRow>
        );
    };

    return (
        <Box sx={{ padding: 2, display: 'flex', flexDirection: 'column', height: '100vh'  }}>
             <AppBar position="static">
                <Toolbar>
                    <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                        Test Name
                    </Typography>
                </Toolbar>
            </AppBar>
            <Box sx={{ flexGrow: 1 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-around', marginBottom: 2 }}>
                    <Box sx={{ textAlign: 'center', padding: 1, backgroundColor: '#f0f0f0', borderRadius: 1 }}>
                        <Typography variant="h6">Total Events</Typography>
                        <Typography variant="h4">{totalCheckboxRows}</Typography>
                    </Box>
                    <Box sx={{ textAlign: 'center', padding: 1, backgroundColor: '#f0f0f0', borderRadius: 1 }}>
                        <Typography variant="h6">Completed Events</Typography>
                        <Typography variant="h4">{checkedCount}</Typography>
                    </Box>
                    <Box sx={{ textAlign: 'center', padding: 1, backgroundColor: '#f0f0f0', borderRadius: 1 }}>
                        <Typography variant="h6">Leftout Events</Typography>
                        <Typography variant="h4">{leftOutCount}</Typography>
                    </Box>
                </Box>
                <FormControl fullWidth sx={{ marginBottom: 2 }}>
                    <InputLabel id="filter-label">Filter Activities</InputLabel>
                    <Select
                        labelId="filter-label"
                        id="filter"
                        value={filter}
                        label="Filter Activities"
                        onChange={handleFilterChange}
                    >
                        <MenuItem value="all">All Activities</MenuItem>
                        <MenuItem value="checked">Checked Activities</MenuItem>
                        <MenuItem value="unchecked">Unchecked Activities</MenuItem>
                    </Select>
                </FormControl>
                <TableContainer component={Paper} sx={{ height: 600, width: '100%' }}>
                    <Table stickyHeader sx={{ minWidth: 650 }}>
                        <TableHead>
                            <TableRow>
                                <TableCell className="table-header-cell" style={{ fontSize: '1.3em' }}>Hrs</TableCell>
                                <TableCell className="table-header-cell" style={{ fontSize: '1.3em' }}>Min</TableCell>
                                <TableCell className="table-header-cell" style={{ fontSize: '1.3em' }}>Sec</TableCell>
                                <TableCell className="table-header-cell" style={{ fontSize: '1.3em' }}>Si. No</TableCell>
                                <TableCell className="table-header-cell" style={{ fontSize: '1.3em' }}>Activity Name</TableCell>
                                <TableCell className="table-header-cell" style={{ fontSize: '1.3em' }}>Confirmed By</TableCell>
                                <TableCell className="table-header-cell" style={{ fontSize: '1.3em' }}>Check</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {filteredData.map((row) => (
                                <Row key={row.id} row={row} />
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
                <Button 
                    variant="contained" 
                    color="secondary" 
                    onClick={handleReset} 
                    sx={{ marginTop: 2 }}
                >
                    {confirmReset === 1 ? "Are you sure you want to reset?" : 
                    confirmReset === 2 ? "Are you really sure you want to reset?" : 
                    "Reset"}
                </Button>
            </Box>
            <Box sx={{ width: '100px', marginLeft: 2, textAlign: 'center' }}>
                <Typography variant="h6">Progress</Typography>
                <Box className="progress-container">
                    <div className="custom-progress-bar" style={{top:'10px'}}>
                        <div className="progress-bar-fill" style={{ height: `${progressPercentage}%` }}></div>
                    </div>
                    <Typography className="progress-percentage" style={{color:'black',top:'20px',fontSize:'1.4em'}}>{`${Math.round(progressPercentage)}%`}</Typography>
                </Box>
            </Box>
        </Box>
    );
};

export default FormattedTable;
