import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { Table, TableHead, TableRow, TableCell, Checkbox, Button, Paper, Select, MenuItem, FormControl, InputLabel, Box, Typography, TableContainer, TableBody } from '@mui/material';
import './FormattedTable.css';

const FormattedTable = () => {
    const location = useLocation();
    const { data } = location.state || { data: [] };
    const [boldRows, setBoldRows] = useState([]);
    const [checkboxRows, setCheckboxRows] = useState([]);
    const [checkedRows, setCheckedRows] = useState([]);
    const [filter, setFilter] = useState('all');
    const [confirmReset, setConfirmReset] = useState(0);

    useEffect(() => {
        const storedBoldRows = JSON.parse(localStorage.getItem('boldRows')) || [];
        const storedCheckboxRows = JSON.parse(localStorage.getItem('checkboxRows')) || [];
        const storedCheckedRows = JSON.parse(localStorage.getItem('checkedRows')) || [];

        setBoldRows(storedBoldRows);
        setCheckboxRows(storedCheckboxRows);
        setCheckedRows(storedCheckedRows);
    }, []);

    useEffect(() => {
        localStorage.setItem('checkedRows', JSON.stringify(checkedRows));
    }, [checkedRows]);

    const handleCheckboxClick = (id) => {
        setCheckedRows((prevCheckedRows) => {
            if (prevCheckedRows.includes(id)) {
                return prevCheckedRows.filter((i) => i !== id);
            } else {
                return [...prevCheckedRows, id];
            }
        });
    };

    const handleReset = () => {
        if (confirmReset === 0) {
            setConfirmReset(1);
        } else if (confirmReset === 1) {
            setConfirmReset(2);
        } else if (confirmReset === 2) {
            setCheckedRows([]);
            setConfirmReset(0);
        }
    };

    const handleFilterChange = (event) => {
        setFilter(event.target.value);
    };

    const getFilteredData = () => {
        switch (filter) {
            case 'checked':
                return data.filter((row) => checkedRows.includes(row.id));
            case 'unchecked':
                return data.filter((row) => !checkedRows.includes(row.id) && checkboxRows.includes(row.id));
            case 'all':
            default:
                return data;
        }
    };

    const filteredData = getFilteredData();
    const totalCheckboxRows = checkboxRows.length;
    const checkedCount = checkedRows.filter(id => checkboxRows.includes(id)).length;
    const leftOutCount = totalCheckboxRows - checkedCount;
    const progressPercentage = totalCheckboxRows > 0 ? (checkedCount / totalCheckboxRows) * 100 : 0;

    const Row = ({ row }) => {
        const isBold = boldRows.includes(row.id);
        const isChecked = checkedRows.includes(row.id);
        return (
            <TableRow key={row.id} className={`${isBold ? 'bold-row' : ''} ${isChecked ? 'checked-row' : ''}`}>
                <TableCell className="table-cell" style={{ fontSize: '1.4em' }}>{row.hrs}</TableCell>
                <TableCell className="table-cell" style={{ fontSize: '1.4em' }}>{row.mins}</TableCell>
                <TableCell className="table-cell" style={{ fontSize: '1.4em' }}>{row.sec}</TableCell>
                <TableCell className="table-cell" style={{ fontSize: '1.4em' }}>{row['si.no']}</TableCell>
                <TableCell className="table-cell wrap-cell" style={{ fontSize: '1.4em' }}>{row.activity_name}</TableCell>
                <TableCell className="table-cell" style={{ fontSize: '1.4em' }}>{row.confirmed_by}</TableCell>
                <TableCell className="table-cell" style={{ fontSize: '1.4em' }}>
                    {checkboxRows.includes(row.id) && (
                        <Checkbox 
                            checked={checkedRows.includes(row.id)} 
                            onChange={() => handleCheckboxClick(row.id)}
                        />
                    )}
                </TableCell>
            </TableRow>
        );
    };

    return (
        <Box sx={{ padding: 2, display: 'flex', flexDirection: 'row', background: 'linear-gradient(to right, #EBF4F6, #37B7C3, #088395, #071952)', minHeight: '100vh', color: '#333' }}>
            <Box sx={{ padding: 2, flexGrow: 1 }}>
                <Box className="sticky-header">
                    <Typography variant="h4" style={{ textAlign: 'center', paddingBottom: '20px', paddingTop: '10px' }}>Activity Tracker</Typography>
                    <Box sx={{ display: 'flex', justifyContent: 'space-around', marginBottom: 2 }}>
                        <Box className="info-box">
                            <Typography variant="h6">Total Events</Typography>
                            <Typography variant="h4">{totalCheckboxRows}</Typography>
                        </Box>
                        <Box className="info-box">
                            <Typography variant="h6">Completed Events</Typography>
                            <Typography variant="h4">{checkedCount}</Typography>
                        </Box>
                        <Box className="info-box">
                            <Typography variant="h6">Leftout Events</Typography>
                            <Typography variant="h4">{leftOutCount}</Typography>
                        </Box>
                    </Box>
                    <FormControl fullWidth sx={{ marginBottom: 2 }}>
                        <InputLabel id="filter-label" style={{ color: '#333' }}>Filter Activities</InputLabel>
                        <Select
                            labelId="filter-label"
                            id="filter"
                            value={filter}
                            label="Filter Activities"
                            onChange={handleFilterChange}
                            style={{ color: '#333' }}
                        >
                            <MenuItem value="all">All Activities</MenuItem>
                            <MenuItem value="checked">Checked Activities</MenuItem>
                            <MenuItem value="unchecked">Unchecked Activities</MenuItem>
                        </Select>
                    </FormControl>
                </Box>

                <TableContainer component={Paper} sx={{ height: 600, width: '100%', backgroundColor: 'rgba(255, 255, 255, 0.9)' }}>
                    <Table stickyHeader sx={{ minWidth: 650 }}>
                        <TableHead>
                            <TableRow>
                                <TableCell className="table-header-cell" style={{ fontSize: '1.4em' }}>Hrs</TableCell>
                                <TableCell className="table-header-cell" style={{ fontSize: '1.4em' }}>Min</TableCell>
                                <TableCell className="table-header-cell" style={{ fontSize: '1.4em' }}>Sec</TableCell>
                                <TableCell className="table-header-cell" style={{ fontSize: '1.4em' }}>Si. No</TableCell>
                                <TableCell className="table-header-cell" style={{ fontSize: '1.4em' }}>Activity Name</TableCell>
                                <TableCell className="table-header-cell" style={{ fontSize: '1.4em' }}>Confirmed By</TableCell>
                                <TableCell className="table-header-cell" style={{ fontSize: '1.4em' }}>Check</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {filteredData.map((row) => (
                                <Row key={row.id} row={row} />
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
                <Button 
                    variant="contained" 
                    color="secondary" 
                    onClick={handleReset} 
                    sx={{ marginTop: 2 }}
                >
                    {confirmReset === 1 ? "Are you sure you want to reset?" : 
                    confirmReset === 2 ? "Are you really sure you want to reset?" : 
                    "Reset"}
                </Button>
            </Box>
            <Box sx={{ width: '100px', marginLeft: 2, textAlign: 'center' }}>
                <Typography variant="h5" style={{ color: 'white', paddingTop: '10px', right: '-60px' }}>Progress</Typography>
                <Box className="progress-container">
                    <div className="custom-progress-bar" style={{ top: '65px', width: '80px' }}>
                        <div className="progress-bar-fill" style={{ height: `${progressPercentage}%` }}></div>
                    </div>
                    <Typography className="progress-percentage" style={{ fontSize: '2.2em' }}>{`${Math.round(progressPercentage)}%`}</Typography>
                </Box>
            </Box>
        </Box>
    );
};

export default FormattedTable;
