import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Table, TableContainer, TableHead, TableRow, TableCell, Checkbox, Button, Paper, Box } from '@mui/material';
import { VariableSizeList as List } from 'react-window';
import './TableSelection.css';

const TableSelection = () => {
    const [data, setData] = useState([]);
    const [boldRows, setBoldRows] = useState([]);
    const [checkboxRows, setCheckboxRows] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:3001/activities');
                setData(response.data);
                console.log('Fetched Data:', response.data); // Log the fetched data
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, []);

    const handleBoldToggle = (id) => {
        setBoldRows(prev => prev.includes(id) ? prev.filter(i => i !== id) : [...prev, id]);
    };

    const handleCheckboxToggle = (id) => {
        setCheckboxRows(prev => prev.includes(id) ? prev.filter(i => i !== id) : [...prev, id]);
    };

    const handleSubmit = () => {
        navigate('/formatted', { state: { data, boldRows, checkboxRows } });
    };

    const calculateRowHeight = (index) => {
        const row = data[index];
        const text = row.activity_name;
        const lineHeight = 20; // Adjust based on your CSS
        const lines = Math.ceil(text.length / 50); // Adjust the divisor based on expected line length
        return 60 + (lines - 1) * lineHeight;
    };

    const getItemSize = useCallback((index) => calculateRowHeight(index), [data]);

    const Row = ({ index, style }) => {
        const row = data[index];
        const isBold = boldRows.includes(row.id);
        return (
            <div style={{ ...style, display: 'grid', gridTemplateColumns: '10% 10% 10% 10% 30% 20% 5% 5%', backgroundColor: index % 2 === 0 ? '#f9f9f9' : '#ffffff' }} className={isBold ? 'bold-row' : ''}>
                <div className="table-cell">{row.hrs}</div>
                <div className="table-cell">{row.mins}</div>
                <div className="table-cell">{row.sec}</div>
                <div className="table-cell">{row['si.no']}</div>
                <div className="table-cell wrap-cell">{row.activity_name}</div>
                <div className="table-cell">{row.confirmed_by}</div>
                <div className="table-cell">
                    <Checkbox 
                        checked={boldRows.includes(row.id)} 
                        onChange={() => handleBoldToggle(row.id)} 
                    />
                </div>
                <div className="table-cell">
                    <Checkbox 
                        checked={checkboxRows.includes(row.id)} 
                        onChange={() => handleCheckboxToggle(row.id)} 
                    />
                </div>
            </div>
        );
    };

    return (
        <Box sx={{ padding: 2, backgroundColor: '#e3f2fd', minHeight: '100vh' }}>
            <TableContainer component={Paper} sx={{ height: 600, width: '100%', marginBottom: 2, boxShadow: 3, borderRadius: 2 }}>
                <Table stickyHeader sx={{ minWidth: 800 }}>
                    <TableHead>
                        <TableRow className="table-header-row">
                            <TableCell className="table-header-cell">Hrs</TableCell>
                            <TableCell className="table-header-cell">Min</TableCell>
                            <TableCell className="table-header-cell">Sec</TableCell>
                            <TableCell className="table-header-cell">Si. No</TableCell>
                            <TableCell className="table-header-cell">Activity Name</TableCell>
                            <TableCell className="table-header-cell">Confirmed By</TableCell>
                            <TableCell className="table-header-cell">Bold</TableCell>
                            <TableCell className="table-header-cell">Checkbox</TableCell>
                        </TableRow>
                    </TableHead>
                </Table>
                <List
                    height={540}
                    itemCount={data.length}
                    itemSize={getItemSize}
                    width="100%"
                    outerElementType={React.forwardRef((props, ref) => <div ref={ref} {...props} />)}
                    innerElementType={React.forwardRef(({ style, ...rest }, ref) => (
                        <div ref={ref} style={{ ...style, display: 'grid', gridTemplateColumns: '10% 10% 10% 10% 30% 20% 5% 5%' }} {...rest} />
                    ))}
                >
                    {({ index, style }) => <Row index={index} style={style} />}
                </List>
            </TableContainer>
            <Button variant="contained" color="primary" onClick={handleSubmit} sx={{ margin: '20px auto', display: 'block' }}>
                Proceed
            </Button>
        </Box>
    );
};

export default TableSelection;
