import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const TableSelection = () => {
    const [data, setData] = useState([]);
    const [boldRows, setBoldRows] = useState([]);
    const [checkboxRows, setCheckboxRows] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:3001/activities');
                setData(response.data);
                console.log('Fetched Data:', response.data); // Log the fetched data
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, []);

    const handleBoldToggle = (index) => {
        setBoldRows(prev => prev.includes(index) ? prev.filter(i => i !== index) : [...prev, index]);
    };

    const handleCheckboxToggle = (index) => {
        setCheckboxRows(prev => prev.includes(index) ? prev.filter(i => i !== index) : [...prev, index]);
    };

    const handleSubmit = () => {
        navigate('/formatted', { state: { data, boldRows, checkboxRows } });
    };

    return (
        <div>
            <table>
                <thead>
                    <tr>
                        <th>Hrs</th>
                        <th>Min</th>
                        <th>Sec</th>
                        <th>Si. No</th>
                        <th>Activity Name</th>
                        <th>Confirmed By</th>
                        <th>Bold</th>
                        <th>Checkbox</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((row, index) => (
                        <tr key={index}>
                            <td>{row.hrs}</td>
                            <td>{row.mins}</td>
                            <td>{row.sec}</td>
                            <td>{row['si.no']}</td>
                            <td>{row.activity_name}</td>
                            <td>{row.confirmed_by}</td>
                            <td>
                                <input 
                                    type="checkbox" 
                                    checked={boldRows.includes(index)} 
                                    onChange={() => handleBoldToggle(index)} 
                                />
                            </td>
                            <td>
                                <input 
                                    type="checkbox" 
                                    checked={checkboxRows.includes(index)} 
                                    onChange={() => handleCheckboxToggle(index)} 
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={handleSubmit}>Submit</button>
        </div>
    );
};

export default TableSelection;
