import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Table, TableContainer, TableHead, TableRow, TableCell, Checkbox, Button, Paper, Box } from '@mui/material';
import './TableSelection.css';

const TableSelection = () => {
    const [data, setData] = useState([]);
    const [boldRows, setBoldRows] = useState([]);
    const [checkboxRows, setCheckboxRows] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:3001/activities');
                setData(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, []);

    const handleBoldToggle = (id) => {
        setBoldRows(prev => prev.includes(id) ? prev.filter(i => i !== id) : [...prev, id]);
    };

    const handleCheckboxToggle = (id) => {
        setCheckboxRows(prev => prev.includes(id) ? prev.filter(i => i !== id) : [...prev, id]);
    };

    const handleSubmit = () => {
        navigate('/formatted', { state: { data, boldRows, checkboxRows } });
    };

    return (
        <Box sx={{ padding: 2 }}>
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Hrs</TableCell>
                            <TableCell>Min</TableCell>
                            <TableCell>Sec</TableCell>
                            <TableCell>Si. No</TableCell>
                            <TableCell>Activity Name</TableCell>
                            <TableCell>Confirmed By</TableCell>
                            <TableCell>Bold</TableCell>
                            <TableCell>Checkbox</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {data.map((row) => (
                            <TableRow key={row.id}>
                                <TableCell>{row.hrs}</TableCell>
                                <TableCell>{row.mins}</TableCell>
                                <TableCell>{row.sec}</TableCell>
                                <TableCell>{row['si.no']}</TableCell>
                                <TableCell>{row.activity_name}</TableCell>
                                <TableCell>{row.confirmed_by}</TableCell>
                                <TableCell>
                                    <Checkbox 
                                        checked={boldRows.includes(row.id)} 
                                        onChange={() => handleBoldToggle(row.id)} 
                                    />
                                </TableCell>
                                <TableCell>
                                    <Checkbox 
                                        checked={checkboxRows.includes(row.id)} 
                                        onChange={() => handleCheckboxToggle(row.id)} 
                                    />
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
            <Button variant="contained" color="primary" onClick={handleSubmit} sx={{ marginTop: 2 }}>
                Proceed
            </Button>
        </Box>
    );
};

export default TableSelection;
