import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Table, TableHead, TableRow, TableCell, TableBody, Box, Paper, Typography } from '@mui/material';

const TableSelection = ({ data }) => {
    const navigate = useNavigate();

    const [selectedBoldRows, setSelectedBoldRows] = useState([]);

    const handleBoldRowClick = (id) => {
        setSelectedBoldRows((prevSelectedBoldRows) => {
            if (prevSelectedBoldRows.includes(id)) {
                return prevSelectedBoldRows.filter((rowId) => rowId !== id);
            } else {
                return [...prevSelectedBoldRows, id];
            }
        });
    };

    const handleSubmit = () => {
        navigate('/formatted-table', { state: { data, boldRows: selectedBoldRows, checkboxRows: [] } });
    };

    return (
        <Box sx={{ padding: 2, minHeight: '100vh', background: 'linear-gradient(180deg, rgb(255, 237, 216), rgb(22, 21, 18))', color: 'rgb(36, 10, 52)' }}>
            <Typography variant="h5" style={{ textAlign: 'center', paddingBottom: '10px', paddingTop: '5px' }}>Table Selection</Typography>
            <Paper sx={{ width: '100%', overflow: 'hidden' }}>
                <Table stickyHeader>
                    <TableHead>
                        <TableRow>
                            <TableCell className="table-header-cell">Hrs</TableCell>
                            <TableCell className="table-header-cell">Min</TableCell>
                            <TableCell className="table-header-cell">Sec</TableCell>
                            <TableCell className="table-header-cell">Si. No</TableCell>
                            <TableCell className="table-header-cell">Activity Name</TableCell>
                            <TableCell className="table-header-cell">Confirmed By</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {data.map((row) => (
                            <TableRow
                                key={row.id}
                                onClick={() => handleBoldRowClick(row.id)}
                                className={row.is_bold || selectedBoldRows.includes(row.id) ? 'bold-row' : ''}
                                style={{ cursor: 'pointer' }}
                            >
                                <TableCell className="table-cell">{row.hrs}</TableCell>
                                <TableCell className="table-cell">{row.mins}</TableCell>
                                <TableCell className="table-cell">{row.sec}</TableCell>
                                <TableCell className="table-cell">{row['si.no']}</TableCell>
                                <TableCell className="table-cell">{row.activity_name}</TableCell>
                                <TableCell className="table-cell">{row.confirmed_by}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </Paper>
            <Button
                variant="contained"
                color="primary"
                onClick={handleSubmit}
                sx={{ marginTop: 2, alignSelf: 'center' }}
            >
                Submit
            </Button>
        </Box>
    );
};

export default TableSelection;
