import React from 'react';
import { useLocation } from 'react-router-dom';
import './FormattedTable.css';

const FormattedTable = () => {
    const location = useLocation();
    const { data, boldRows, checkboxRows } = location.state;

    return (
        <div className="formatted-table-container">
            <table>
                <thead>
                    <tr>
                        <th>Hrs</th>
                        <th>Min</th>
                        <th>Sec</th>
                        <th>Si. No</th>
                        <th>Activity Name</th>
                        <th>Confirmed By</th>
                        <th>Check</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((row, index) => (
                        <tr key={index} style={{ fontWeight: boldRows.includes(index) ? 'bold' : 'normal' }}>
                            <td>{row.hrs}</td>
                            <td>{row.mins}</td>
                            <td>{row.sec}</td>
                            <td>{row['si.no']}</td>
                            <td>{row.activity_name}</td>
                            <td>{row.confirmed_by}</td>
                            <td>
                                <input 
                                    type="checkbox" 
                                    checked={checkboxRows.includes(index)} 
                                    readOnly 
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="progress-container">
                <label>Progress:</label>
                <progress value={(checkboxRows.length / data.length) * 100} max="100"></progress>
            </div>
        </div>
    );
};

export default FormattedTable;
